import logo from './logo.svg';
import './App.css';
import "bootstrap/dist/css/bootstrap.min.css";
import {nanoid} from 'nanoid'; 
import React, {useState} from 'react';

function App() {

const [manga,setManga] = useState(null);

  const mangaAndCreator = [
    {id:nanoid(), name: 'JoJo Bizarre Adventure', by: 'Hirohiko Araki', image:'images/'},
    {id:nanoid(), name: 'Deadman Wonderland', by: 'Jinsei Kataoka', image:'images/'},
    {id:nanoid(), name: 'Flcl', by: 'Kazuya Tsurumaki and Yōji Enokido', image:'images/'},
    {id:nanoid(), name: 'Case Closed', by: 'Gosho Aoyama', image:'images/'},
    {id:nanoid(), name: 'The way of a Househusband', by: 'Kousuke Oono', image:'images/'},
    {id:nanoid(), name: 'Blue Exorcist', by: 'Kazue Kato', image:'images/'},
    {id:nanoid(), name: 'Letter Bee', by: 'Hiroyuki Asada', image:'images/'},
    {id:nanoid(), name: 'Monster', by: 'Naoki Urasawa', image:'images/'},
    {id:nanoid(), name: 'Berserk', by: 'Kentaro Miura and Kouji Mori', image:'images/'},
    {id:nanoid(), name: 'Pandora Hearts', by: 'Jun Mochizuki', image:'images/'},
    {id:nanoid(), name: 'Death Note', by: 'Tsugumi Ohba', image:'images/'}
  ];
  return (
    <div className='container'>
      <div className='row'>
        <div className='col-lg-2'>
          <div className='card'>
            <img src="images/jojo.jpg" alt='broken' />
            <ul className="list-group list-group-flush">
              <li className='list-group-item'>1</li>
              <li className='list-group-item'>2</li>
            </ul>
          </div>
        </div>
        <div className='col-lg-4'>col2</div>
        <div className='col-lg-4'>col3</div>
        <div className='col-lg-4'>col4</div>
        <div className='col-lg-4'>col5</div>
        <div className='col-lg-4'>col6</div>
        <div className='col-lg-4'>col7</div>
        <div className='col-lg-4'>col8</div>
        <div className='col-lg-4'>col9</div>
        <div className='col-lg-4'>col10</div>
        <div className='col-lg-4'>col11</div>
      </div>
      {!manga && <button type='button' className='btn btn-lg btn-success' onClick={() => setManga()}>Save Manga</button>}
    </div>
  );
}

export default App;
