import logo from './logo.svg';
import './App.css';
import "bootstrap/dist/css/bootstrap.min.css";
import {nanoid} from 'nanoid'; 
import React, {useState} from 'react';

function App() {

const [manga,setManga] = useState(null);

  const mangaAndCreator = [
    {id:nanoid(), name: 'JoJo Bizarre Adventure', by: 'Hirohiko Araki', image:'images/jojo.jpg', images:'images/Hirohiko Araki.jpg'},

    {id:nanoid(), name: 'Deadman Wonderland', by: 'Jinsei Kataoka', image:'images/Deadman Wonderland.jpg', images:'images/Jinsei Kataoka.jpg'},

    {id:nanoid(), name: 'Flcl', by: 'Kazuya Tsurumaki and Yōji Enokido', image:'images/Flcl.jpg', images:'images/Kazuya Tsurumaki.jpg', img:'images/Yōji Enokido.jpg'},

    {id:nanoid(), name: 'Case Closed', by: 'Gosho Aoyama', image:'images/Case Closed.jpg', images:'images/Gosho Aoyama.jpg'},

    {id:nanoid(), name: 'The way of a Househusband', by: 'Kousuke Oono', image:'images/The way of a Househusband.jpg', images:'images/Kousuke Oono.jpg'},

    {id:nanoid(), name: 'Blue Exorcist', by: 'Kazue Kato', image:'images/Blue Exorcist.jpg', images:'images/Kazue Kato.jpg'},

    {id:nanoid(), name: 'Letter Bee', by: 'Hiroyuki Asada', image:'images/Letter Bee.jpg', images:'images/Hiroyuki Asada.jpg'},

    {id:nanoid(), name: 'Monster', by: 'Naoki Urasawa', image:'images/Monster.jpg', images:'images/Naoki Urasawa.jpg'},

    {id:nanoid(), name: 'Berserk', by: 'Kentaro Miura and Kouji Mori', image:'images/Berserk.jpg', images:'images/Kentaro Miura.jpg', img:'images/Kouji Mori.jpg'},

    {id:nanoid(), name: 'Pandora Hearts', by: 'Jun Mochizuki', image:'images/Pandora Hearts.jpg', images:'images/Jun Mochizuki.jpg'},

    {id:nanoid(), name: 'Death Note', by: 'Tsugumi Ohba', image:'images/Death Note.jpg', images:'images/Tsugumi Ohba.jpg'}
  ];
  return (
    <div className='container'>
      <div className='row'>
        {manga && manga.map((mangaShow) => 
        (
        <div className='col-md-2' key={mangaShow.name}>
        <div className='card'>
          <img src={mangaShow.image} alt='broken' />
          <ul className="list-group list-group-flush">
            <li className='list-group-item'>{mangaShow.name}</li>
            <li className='list-group-item'>{mangaShow.by}</li>
          </ul>


        </div>
      </div>)
        )}


      </div>
      {!manga && <button type='button' className='btn btn-lg btn-success' onClick={() => setManga(mangaAndCreator) }>Show Manga</button>}
    </div>
  );
}

export default App;
